
function soma(var1,var2){
    return var1 + var2; 
}

function sub(var1,var2){
    return var1 - var2; 
}

function div(var1,var2){
    return var1 / var2; 
}
function mult(var1,var2){
    return var1 * var2; 
}

module.exports = {
    soma,
    sub,
    div,
    mult
};// transformando multiplas funcões em um modulo para ser possivel a utilização em outro arquivo